import React, { useEffect, useState, useRef } from "react";
import Peer from "peerjs";
import VideoPlayer from "./VideoPlayer";

const Room = ({ username, onLogout }) => {
  const [myStream, setMyStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [peerIdToCall, setPeerIdToCall] = useState("");
  const [myPeerId, setMyPeerId] = useState("");
  const peerRef = useRef(null);

  useEffect(() => {
    async function init() {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setMyStream(stream);

        peerRef.current = new Peer(undefined, {
          host: process.env.REACT_APP_PEER_SERVER_HOST,
          port: Number(process.env.REACT_APP_PEER_SERVER_PORT),
          path: process.env.REACT_APP_PEER_SERVER_PATH,
        });

        peerRef.current.on("open", (id) => {
          setMyPeerId(id);
          console.log("My peer ID:", id);
        });

        peerRef.current.on("call", (call) => {
          call.answer(stream);
          call.on("stream", (remoteStream) => {
            setRemoteStreams((prev) => {
              if (!prev.some(s => s.id === remoteStream.id)) {
                return [...prev, remoteStream];
              }
              return prev;
            });
          });
        });

        peerRef.current.on("error", (err) => {
          console.error(err);
        });
      } catch (err) {
        alert("Could not get user media");
      }
    }
    init();

    return () => {
      if (peerRef.current) peerRef.current.destroy();
      if (myStream) myStream.getTracks().forEach(track => track.stop());
    };
  }, []);

  const callPeer = () => {
    if (!peerIdToCall) return;

    const call = peerRef.current.call(peerIdToCall, myStream);
    call.on("stream", (remoteStream) => {
      setRemoteStreams((prev) => {
        if (!prev.some(s => s.id === remoteStream.id)) {
          return [...prev, remoteStream];
        }
        return prev;
      });
    });
  };

  return (
    <div style={{ padding: 20 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Welcome, {username}!</h2>
        <button onClick={onLogout}>Logout</button>
      </div>

      <p>Your Peer ID: <b>{myPeerId}</b></p>

      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Enter peer ID to call"
          value={peerIdToCall}
          onChange={(e) => setPeerIdToCall(e.target.value)}
          style={{ marginRight: 10, padding: "6px 8px" }}
        />
        <button onClick={callPeer}>Call</button>
      </div>

      <div>
        <h3>Your Video</h3>
        {myStream && <VideoPlayer stream={myStream} isMuted={true} />}
      </div>

      <div>
        <h3>Remote Videos</h3>
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {remoteStreams.map((stream, idx) => (
            <VideoPlayer key={idx} stream={stream} isMuted={false} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Room;
