import React, { useEffect, useState, useRef } from "react";
import Peer from "peerjs";
import VideoPlayer from "./VideoPlayer";
import { FaMicrophone, FaMicrophoneSlash, FaVideo, FaVideoSlash, FaDesktop, FaStop, FaPhone } from "react-icons/fa";

const Room = ({ username, onLogout }) => {
  const [myStream, setMyStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [peerIdToCall, setPeerIdToCall] = useState("");
  const [myPeerId, setMyPeerId] = useState("");
  const [muted, setMuted] = useState(false);
  const [videoOff, setVideoOff] = useState(false);
  const [isScreenSharing, setIsScreenSharing] = useState(false);

  const peerRef = useRef(null);
  const currentCallRefs = useRef([]);
  const screenStreamRef = useRef(null);

  useEffect(() => {
    const init = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setMyStream(stream);
        peerRef.current = new Peer();

        peerRef.current.on("open", id => setMyPeerId(id));
        peerRef.current.on("call", call => {
          call.answer(stream);
          currentCallRefs.current.push(call);
          call.on("stream", remoteStream => {
            setRemoteStreams(prev => prev.some(s => s.id === remoteStream.id) ? prev : [...prev, remoteStream]);
          });
          call.on("close", () => setRemoteStreams(prev => prev.filter(s => s.id !== call.remoteStream?.id)));
        });
        peerRef.current.on("error", err => console.error(err));
      } catch (err) {
        alert("Could not access camera or microphone.");
      }
    };
    init();
    return () => {
      peerRef.current?.destroy();
      myStream?.getTracks().forEach(track => track.stop());
      screenStreamRef.current?.getTracks().forEach(track => track.stop());
    };
  }, []);

  // ...Toggle and share handlers as in previous code (see prior message for full functions)...

  // UI below
  return (
    <div className="video-room-container">
      <header>
        <span>Welcome, <b>{username}</b> | Your Peer ID: <code style={{ background: "#eee", padding: "2px 6px", borderRadius: 4 }}>{myPeerId}</code></span>
        <button className="leave-call" onClick={leaveCall} title="Leave Call"><FaPhone /> Leave</button>
      </header>

      <div className="call-peer-panel">
        <input
          placeholder="Enter Peer ID"
          value={peerIdToCall}
          onChange={e => setPeerIdToCall(e.target.value)}
          className="input-peerid"
        />
        <button onClick={callPeer} className="call-peer-btn">Call</button>
      </div>

      <div className={`videos-grid ${isScreenSharing ? "screen-sharing" : ""}`}>
        {myStream && <VideoPlayer stream={myStream} isMuted={true} />}
        {remoteStreams.map((stream, idx) => (
          <VideoPlayer key={idx} stream={stream} isMuted={false} />
        ))}
      </div>

      {/* Toolbar */}
      <div className="toolbar">
        <button
          className={`toolbar-btn ${muted ? "danger" : ""}`}
          title={muted ? "Unmute Mic" : "Mute Mic"}
          onClick={toggleMute}
        >
          {muted ? <FaMicrophoneSlash /> : <FaMicrophone />}
        </button>
        <button
          className={`toolbar-btn ${videoOff ? "danger" : ""}`}
          title={videoOff ? "Start Video" : "Stop Video"}
          onClick={toggleVideo}
        >
          {videoOff ? <FaVideoSlash /> : <FaVideo />}
        </button>
        <button
          className="toolbar-btn info"
          title={isScreenSharing ? "Stop Share" : "Share Screen"}
          onClick={isScreenSharing ? stopScreenShare : startScreenShare}
        >
          {isScreenSharing ? <FaStop /> : <FaDesktop />}
        </button>
      </div>
    </div>
  );
};

export default Room;
